// 大于小于
$ db.users.find({&quot;age&quot;: {&quot;$gte&quot;: 18, &quot;$lte&quot;: 30}}) // 大于等于18小于等于30岁
db.users.find({&quot;registered&quot;: {&quot;$lt&quot;: new Date(&quot;01/01/2014&quot;)}}); // 在2014/01/01前注册
// 不等于
